configure() { # Place #defines in include/config.h relative # to location of MakeKitBuild in source tree mk_config_header "include/config.h"
# Define FOOBAR to 1 mk_define FOOBAR 1
# Define BUILD_TIME to current time as a string # using UNIX 'date' program mk_define BUILD_TIME "\"`date`\"" }

configure() { mk_config_header "include/config.h"
# Check for some basic header files mk_check_headers stdio.h stdlib.h string.h does-not-exist.h
# On a reasonable system, the following variables will be set: # # HAVE_STDIO_H=external # HAVE_STDLIB_H=external # HAVE_STRING_H=external # HAVE_DOES_NOT_EXIST_H=no # # The following defines will be written into config.h: # # #define HAVE_STDIO_H # #define HAVE_STDLIB_H # #define HAVE_STRING_H }

configure() { mk_config_header "include/config.h"
# Check for some common UNIX libraries mk_check_libraries m pthread does-not-exist
# On a typical Linux system, the following variables will be set: # # HAVE_LIB_M=external # HAVE_LIB_PTHREAD=external # HAVE_LIB_DOES_NOT_EXIST=no # LIB_M=m # LIB_PTHREAD=pthread # LIB_DOES_NOT_EXIST= # # The following defines will be written into config.h: # # #define HAVE_LIB_M # #define HAVE_LIB_PTHREAD }

configure() { mk_config_header "include/config.h"
# Check for some functions in libm mk_check_functions \ HEADERDEPS="math.h" \ LIBDEPS="m" \ sin cos "double sqrt(double)" does_not_exist
# On a typical UNIX system, the following variables will be set: # # HAVE_SIN=yes # HAVE_COS=yes # HAVE_DOUBLE_SQRT__DOUBLE_=yes # HAVE_DOES_NOT_EXIST=no # # The following defines will be written into config.h: # # #define HAVE_SIN # #define HAVE_COS # #define HAVE_DOUBLE_SQRT__DOUBLE_ # #define HAVE_DECL_SIN 1 # #define HAVE_DECL_COS 1 # #define HAVE_DECL_DOUBLE_SQRT__DOUBLE_ 1 # #define HAVE_DECL_DOES_NOT_EXIST 0 }

configure() { mk_config_header "include/config.h"
# Check for some types in stdlib.h and inttypes.h mk_check_types \ HEADERDEPS="stdlib.h inttypes.h" \ size_t int64_t uint64_t does_not_exist_t
# On a typical UNIX system, the following variables will be set: # # HAVE_SIZE_T=yes # HAVE_INT64_T=yes # HAVE_UINT64_T=yes # HAVE_DOES_NOT_EXIST_T=no # # The following defines will be written into config.h: # # #define HAVE_SIZE_T # #define HAVE_INT64_T # #define HAVE_UINT64_T }

